fun main() {
    var t = readln().toInt()
    while (t-- > 0) {
        val s = readln()
        val n = s.length
        var il = 0
        while (il < n && s[il] == '0') il++
        val ans = IntArray(4) { 1 }
        if (il == n) {
//            println("s1 is all 0s")
            println(ans.joinToString(" "))
            continue
        }
        ans[0] = il + 1
        val ir = n - 1
        ans[1] = ir + 1
        var jl = il + 1
        while (jl < n && s[jl] == '1') jl++
        if (jl == n) {
//            println("s1 is all 1s")
            jl = 0
            while (jl < il && s[jl] == '1') jl++
            if (jl == il) {
                ans[2] = 1
                ans[3] = 1
            } else {
                ans[2] = jl + 1
                ans[3] = jl + 1
            }
            println(ans.joinToString(" "))
            continue
        }

        for (p in il + 1..ir) {
            if (s[p] == '1') continue
            val s2length = ir - p + 1
            var maxDiff = 0
            for (jl in 0 until n - s2length) {
                var currDiff = 0
                var k = 0
                while (k < s2length && s[p + k] != s[jl + k]) k++
                currDiff = k
                if (maxDiff < currDiff) {
                    maxDiff = currDiff
                    // found
                    ans[2] = jl + 1
                    ans[3] = jl + s2length
                }
            }
            if (maxDiff != 0) break
        }

        println(ans.joinToString(" "))
    }
}

